//TIP To <b>Run</b> code, press <shortcut actionId="Run"/> or
// click the <icon src="AllIcons.Actions.Execute"/> icon in the gutter.
public class Main {
    public static void main(String[] args) {
        Partida partida = new Partida();

        try {
            System.out.println(partida.agregarJugador("Jugador1"));
            System.out.println(partida.agregarJugador("Jugador2"));
        } catch (Exception e) {
            System.err.println("Error al agregar jugadores: " + e.getMessage());
        }

        try {
            String resultado = partida.iniciarPartida();
            System.out.println(resultado);
        } catch (Exception e) {
            System.err.println("Error al iniciar la partida: " + e.getMessage());
        }
    }
}

